export class ViewDayComponent {
  constructor(activeRoute, router, store, udService, cService) {
    this.activeRoute = activeRoute;
    this.router = router;
    this.store = store;
    this.udService = udService;
    this.cService = cService;
    this.date = new Date(+this.activeRoute.snapshot.params['date']);
    this.time = this.activeRoute.snapshot.params['time'];
    this.meals = [];
    this.meals$ = this.cService.mealsArr;
    this.user$ = this.udService.user$;
    this.title = '';
    this.summaryfats = 0;
    this.summaryprotein = 0;
    this.summarycarbohydrates = 0;
    this.summarykcal = 0;
  }

  ngOnInit() {
    console.log(this.isDay);

    if (!this.isDay) {
      const newDate = new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate());
      newDate.setHours(0, 0, 0, 0);

      if (+this.date === +newDate) {
        this.title = 'Today';
      } else {
        const time = new Date(+this.date);
        let header = Months[time.getMonth()];
        header = header.substr(0, 1).toUpperCase() + header.substr(1);
        this.title = `${header}-${time.getDate()}`;
      }
    }
  }

  generateMealsList(mealsArr) {
    mealsArr.forEach(value => {
      if (+this.date === +value.date) {
        this.meals.push(value);
      }
    });
    this.meals.forEach(value => {
      this.summaryfats += +value.fats;
      this.summaryprotein += +value.proteins;
      this.summarycarbohydrates += +value.carbohydrates;
      this.summarykcal += +value.kcal;
    });
    return this.meals;
  }

  deleteMeal() {
    this.store.dispatch(removeMealAction({
      date: this.date,
      time: this.time
    }));
    this.router.navigate(['/calendar']);
  }

  deleteMealFromList(meal) {
    this.store.dispatch(removeMealAction({
      date: meal.date,
      time: meal.time
    }));
    this.meals = this.meals.filter(obj => obj !== meal);
  }

}

